static bool test_huffman_encode( test_ctx_t* ctx )
{
	bool result = true;
	char* compressed = NULL;
	size_t compressed_size = 0;

	bool huffman_result = huffman_encode( reference_text, reference_text_size, (void**) &compressed, &compressed_size );

	if( !test_assert_true( ctx, huffman_result, "Expected huffman_encode() to succeed." ) )
	{
		result = false;
		goto done;
	}

	if( !test_assert_true( ctx, compressed_size < reference_text_size, "Expected output (%zu) to be smaller than input (%zu).", compressed_size, reference_text_size ) )
	{
		result = false;
		goto done;
	}

	if( !test_assert_memory_equals( ctx, compressed, reference_text_compressed, compressed_size, "Expected memory content to be equal." ) )
	{
		result = false;
		goto done;
	}

done:
	if (compressed) free(compressed);
	return result;
}

static bool test_huffman_decode( test_ctx_t* ctx )
{
	bool result = true;
	char* original = NULL;
	size_t original_size = 0;

	bool huffman_result = huffman_decode( reference_text_compressed, reference_text_compressed_size, (void**) &original, &original_size );

	if( !test_assert_true( ctx, huffman_result, "Expected huffman_decode() to succeed." ) )
	{
		result = false;
		goto done;
	}

	if( !test_assert_true( ctx, original_size > reference_text_compressed_size, "Expected output (%zu) to be larger than input (%zu).", original_size, reference_text_compressed_size ) )
	{
		result = false;
		goto done;
	}

	if( !test_assert_memory_equals( ctx, reference_text, original, original_size, "Expected memory content to be equal." ) )
	{
		result = false;
		goto done;
	}

done:
	if (original) free(original);
	return result;
}

static bool test_base64_encode( test_ctx_t* ctx )
{
	bool result = true;
	const char text[] = "This is a test.";
	size_t encoded_len = 0;
	char* base64_encoded = base64_encode( text, sizeof(text), &encoded_len);

	if( !test_assert_not_null( ctx, base64_encoded, "Expected output to not be null." ) )
	{
		result = false;
		goto done;
	}

	if( !test_assert_true( ctx, encoded_len == 24, "Expected output to have a length of 24 but length was %zu", encoded_len ) )
	{
		result = false;
		goto done;
	}

	if( !test_assert_equals( ctx, base64_encoded, "VGhpcyBpcyBhIHRlc3QuAA==", "Expected output to match \"VGhpcyBpcyBhIHRlc3Qu\"" ) )
	{
		result = false;
		goto done;
	}

done:
	if( base64_encoded ) free(base64_encoded);
	return result;
}

static bool test_base64_decode( test_ctx_t* ctx )
{
	bool result = true;
	const char base64[] = "Qm90aCBQZXJ1IGFuZCBDaGlsZSBhcmUgYWxzbyByZXBvcnRpbmcgYSBoaWdoIGluY2lkZW5jZQ==";
	size_t text_len = 0;

	char* text = base64_decode( base64, strlen(base64), &text_len );

	if( !test_assert_not_null( ctx, text, "Expected output to not be null." ) )
	{
		result = false;
		goto done;
	}

	if( !test_assert_true( ctx, text_len == 55, "Expected output to have a length of 55 but length was %zu", text_len ) )
	{
		result = false;
		goto done;
	}

	if( !test_assert_equals( ctx, text, "Both Peru and Chile are also reporting a high incidence", "Expected output to match \"Both Peru and Chile are also reporting a high incidence\"" ) )
	{
		result = false;
		goto done;
	}

done:
	if( text ) free(text);
	return result;
}


const char* ENCODING_TEST_DESCRIPTION = "Testing functions in <xtd/encoding.h>";
const test_case_t ENCODING_TEST_CASES[] = {
	{ "Test huffman_encode()", test_huffman_encode },
	{ "Test huffman_decode()", test_huffman_decode },
	{ "Test base64_encode()", test_base64_encode },
	{ "Test base64_decode()", test_base64_decode },
};
const size_t ENCODING_TEST_COUNT = sizeof(ENCODING_TEST_CASES) / sizeof(ENCODING_TEST_CASES[0]);

#ifdef TEST_ENCODING
int main(int argc, char* argv[])
{
	size_t pass_count = test_features( ENCODING_TEST_DESCRIPTION, ENCODING_TEST_CASES, ENCODING_TEST_COUNT, true );
	return ENCODING_TEST_COUNT - pass_count;
}
#endif
